mapper.update:
update tb_user
user_name = '${user_name}', user_photo = '${user_photo}', user_score_a = '${user_score_a}', user_score_b = '${user_score_b}', user_step = '${user_step}', user_tickets = '${user_tickets}', user_be_liked = ${user_be_liked}, user_likes_today = ${user_likes_today}, user_likes_no = '${user_likes_no}', user_likes_total = ${user_likes_total}, last_refresh_time = '${last_refresh_time}', phone_no = '${phone_no}', address = '${address}',
where user_no = ${user_no}

mapper.select:
SELECT user_no, user_name, user_photo, user_be_liked FROM tb_user
user_no IN #{id}
ORDER BY user_be_liked DESC
