mapper.update:
update tb_user
user_photo = ${user_photo}, user_score = '${user_score}', user_step = '${user_step}', user_tickets = '${user_tickets}', user_be_liked = ${user_be_liked}, user_likes_today = ${user_likes_today}, user_likes_total = ${user_likes_total}, last_refresh_time = ${last_refresh_time},
where user_no = ${user_no}
